function RAW_CF_Rest()
  if RAW_GetModOptionEnabled(RAW_Globals.features.RestShort) and Ext.Mod.IsModLoaded(RAW_ModCompatibilityFramework) then
    Ext.Events.StatsLoaded:Subscribe(function(e)
      local value = RAW_GetModOptionValue(RAW_Globals.features.RestShort)
      if value == "Simple" then
        applyRestSimpleChanges()
      else
        applyRestRAWChanges()
      end
    end)
  end
end